import React, { Component } from 'react';


class Comments extends Component {
    render() {
        return (
            <section className="comment__section">
        <div className="comment__section--title">Join the Conversation</div>
        <form id="commentForm" className="comment--form">
            <fieldset className="comment--form__fieldset">
                <div id="comment--form__img"></div>
                <label id="comment" for="commentText" >COMMENT</label>
                <textarea id="textarea" type="textarea"  name="textarea" placeholder="Add a new comment" rows="5" cols="40" value='textarea' ></textarea>
            </fieldset>
            <div  className="comment__section--button" >
                <button className="comment__section--button--here" name="submit">COMMENT</button>
                </div>
        </form>
        </section>
        )
    }
}
export default Comments;
