import './App.css';
import './App.scss';
import React from "react";
import Header from './Components/Header';
import Videos from './Components/Videos';
import VideoDes from './Components/VideoDescrib';
import Comments from './Components/Comments';
import VideoQue from './Components/VideoQue'
import VideoDetails from './assets/Data/video-details.json';





export class App extends React.Component {
  state = {
    currentVideo: VideoDetails[0],
    quedVideos: VideoDetails,
  }

  changeVideo(Video) {
    this.setState({Video})
  }

  handleChange(e){
    console.log(this.props.quedVideos.id)
        this.setState({
            currentVideo: this.props.quedVideos.id,
        })
}

  render() {
      return (
  <div>
  <Header></Header>
  <Videos currentVideo = {this.state.currentVideo} > </Videos>
  <VideoDes currentVideo = {this.state.currentVideo}></VideoDes>
  <Comments currentVideo = {this.state.currentVideo} ></Comments>
  <VideoQue handleChange={this.handleChange.bind(this)} changeVideo={this.changeVideo.bind(this)} currentVideo={this.state.currentVideo} quedVideos= {this.state.quedVideos}></VideoQue>  
</div>
      )
  }
}


export default App;
