import React, { Component } from 'react';


class Comments extends Component {
    render() {
        return (
            <section class="comment__section">
        <h1 class="comment__section--title">Join the Conversation</h1>
        <form id="commentForm" class="comment--form">
            <fieldset class="comment--form__fieldset">
                <div id="comment--form__img"></div>
                <label id='name' for="Name">NAME</label>
                <input type="text" id="name__form" name='name' placeholder="Enter your name"
                required /> 
                <label id="comment" for="commentText" >COMMENT</label>
                <textarea id="textarea" type="textarea"  name="textarea" placeholder="Add a new comment" rows="5" cols="40" value='textarea' ></textarea>
            </fieldset>
            <div  class="comment__section--button" >
                <button class="comment__section--button--here" name="submit">COMMENT</button>
                </div>
        </form>
        </section>
        )
    }
}
export default Comments;
